/* eslint-disable no-unused-vars */
import React from 'react';
import styled from 'styled-components/macro';
import { DialogOverlay, DialogContent } from '@reach/dialog';

import UnstyledButton from '../UnstyledButton';
import Icon from '../Icon';
import VisuallyHidden from '../VisuallyHidden';

const MobileMenu = ({ isOpen, onDismiss }) => {
  return (
    <Overlay isOpen={isOpen} onDismiss={onDismiss}>
      <Content>
        {/* <button onClick={onDismiss}>Dismiss menu</button> */}
        <Filler />
        <Nav>
          <NavLin href="/sale">Sale</NavLin>
          <NavLin href="/new">New&nbsp;Releases</NavLin>
          <NavLin href="/men">Men</NavLin>
          <NavLin href="/women">Women</NavLin>
          <NavLin href="/kids">Kids</NavLin>
          <NavLin href="/collections">Collections</NavLin>
        </Nav>
        <Footer>
          <NavLin href="/terms">Terms and Conditions</NavLin>
          <NavLin href="/privacy">Privacy Policy</NavLin>
          <NavLin href="/contact">Contact Us</NavLin>
        </Footer>
      </Content>
    </Overlay>
  );
};

const Overlay = styled(DialogOverlay)`
  position: fixed;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  background-color: hsla(220deg 5% 40% / 0.8);
  display: flex;
  justify-content: flex-end;
`;

const Content = styled(DialogContent)`
  background-color: white;
  height: 100%;
  width: 300px;
  padding: 32px;
  display: flex;
  flex-direction: column;
`;

const Nav = styled.nav`
  display: flex;
  flex-direction: column;
`;

const Filler = styled.div`
  flex: 1;
`;

const Footer = styled.footer`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`;

export default MobileMenu;
